mapper.select:
SELECT 

FROM jh_fj_gr_rw a

WHERE a.id = #{id}

SELECT 

FROM jh_fj_gr_rw a


	a.del_flag = #{DEL_FLAG_NORMAL}



	and a.jh_fj_id = #{jhFjId}


		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

SELECT 

FROM jh_fj_gr_rw a


	a.del_flag = #{DEL_FLAG_NORMAL}



		ORDER BY ${page.orderBy}


		ORDER BY a.update_date DESC

mapper.update:
UPDATE jh_fj_gr_rw SET 
	del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}

mapper.select:
SELECT rw.id,rw.jh_fj_id, fj.jh_id as 'jhId', fj.js_id as 'jsId', rw.sf,dq1.name as 'sfName', rw.cs,dq2.name as 'csName', rw.qx,dq3.name as 'qxName', rw.xz,dq4.name as 'xzName' FROM jh_fj_gr_rw rw JOIN jh_fj_gr fj ON rw.jh_fj_id = fj.id LEFT JOIN sys_area dq1 ON dq1.id = rw.sf LEFT JOIN sys_area dq2 ON dq2.id = rw.cs LEFT JOIN sys_area dq3 ON dq3.id = rw.qx LEFT JOIN sys_area dq4 ON dq4.id = rw.xz WHERE rw.del_flag = 0
and rw.jh_fj_id = #{jhFjId}
and rw.sf = #{sf}
and rw.cs = #{cs}
and rw.qx = #{qx}
ORDER BY js_id,sf,cs,qx,xz
